--######################################################################################################--
--######################################################################################################--

local SensSetting = 1 --> !! Change it for your own "Vertical Sensitivity Multiplier" Setting.

-- swap crouch (LSHIFT) and zoom (LCTRL) safely
local _IsMod = IsModifierPressed  -- save Logitech's original

function IsModifierPressed(key)
    if key == "lctrl" then
        return _IsMod("lshift")   -- old crouch check now reads LSHIFT
    elseif key == "lshift" then
        return _IsMod("lctrl")    -- old zoom check now reads LCTRL
    else
        return _IsMod(key)
    end
end

-- Define a safe wrapper for the Logitech function if not already declared
function LogitechIsModifierPressed(key)
    return _G._IsModifierPressed and _G._IsModifierPressed(key) or IsModifierPressedOriginal(key)
end

-- Save original function reference if it exists
if not _G._IsModifierPressed then
    _G._IsModifierPressed = IsModifierPressed
end


